package com.student.action;import java.util.List;import com.student.entity.Students;import com.student.service.StudentsDao;import com.student.service.impl.StudentsDaoImpl;public class StudentsAction extends SuperAction {	/**	 * 	 */	private static final long serialVersionUID = 1L;		public String query(){		StudentsDao studentsDao = new StudentsDaoImpl();		List<Students> list = studentsDao.queryAllStudents();				if(list != null && list.size() > 0){			session.setAttribute("students_list", list);			}		return "students_query_success";	}	}