/*删除商城数据库,如果存在*/drop database if exists studenttest;/*创建数据库,并设置编码*/create database studenttest default character set utf8;use studenttest;create table students(	sid varchar(8) not null,	sname      varchar(255),	gender     varchar(255),	birthday   date,	address    varchar(255),	primary key(sid)	);create table users(	uid integer not null auto_increment,	username varchar(255),	password varchar(255),	primary key(uid)	);insert into users(username,password)values('jinwei','jinwei');select * from users;select * from students;